package com.alphalogicadmin.model;

public class SupplierPersonalInfo {

	public SupplierPersonalInfo() {
		// TODO Auto-generated constructor stub
	}
	
	private String sPId;
	private String sPhoneNo;
	private String sCity;
	private String sAddress;
	private String sPostalCode;
	public String getsPId() {
		return sPId;
	}
	public void setsPId(String sPId) {
		this.sPId = sPId;
	}
	public String getsPhoneNo() {
		return sPhoneNo;
	}
	public void setsPhoneNo(String sPhoneNo) {
		this.sPhoneNo = sPhoneNo;
	}
	public String getsCity() {
		return sCity;
	}
	public void setsCity(String sCity) {
		this.sCity = sCity;
	}
	public String getsAddress() {
		return sAddress;
	}
	public void setsAddress(String sAddress) {
		this.sAddress = sAddress;
	}
	public String getsPostalCode() {
		return sPostalCode;
	}
	public void setsPostalCode(String sPostalCode) {
		this.sPostalCode = sPostalCode;
	}
	
	

}
